automation.executionScript:
if (core_v2_widget.GetBoolValue('ignoreIfOriginalAuthor', true) && context_v2_automationTrigger.Arguments.DownloaderId == context_v2_automationTrigger.Arguments.Author.Id)
{
    // file author is the same as downloader
    return;
}

var userId = context_v2_automationTrigger.Arguments.DownloaderId;
if (core_v2_widget.GetStringValue('awardTo', 'parent') == 'parent') {
    userId = context_v2_automationTrigger.Arguments.Author.Id;
}

context_v1_pointsAutomation.AwardPoints(userId, context_v2_automationTrigger.Arguments.ContentId, core_v2_media.ContentTypeId);
