    				if(isLoadingMore || !hasMore)
    					return;

    				isLoadingMore = true;

    				var data = {}
    				data['$core_v2_encoding.JavascriptEncode($core_v2_ui.PageIndexQueryStringKey)'] = pageIndex + 2;

    				j.telligent.evolution.get({
